import React from "react";
import Work from "../Components/Work";
import { personalDetails, workDetails, eduDetails } from "../Details";

function About() {
  return (
    <main className="container mx-auto max-w-7xl pt-10 pb-20">
      {/* About Me Section */}
      <section>
        <h1 className="text-2xl text-dark-heading dark:text-light-heading md:text-4xl xl:text-5xl font-bold">
          About Me
        </h1>
        <p className="text-content py-8 lg:max-w-3xl">{personalDetails.about}</p>
      </section>

      {/* Work Experience Section */}
      <section>
        <h1 className="text-2xl text-dark-heading dark:text-light-heading md:text-4xl xl:text-5xl font-bold">
          Work Experience
        </h1>
        {React.Children.toArray(
          workDetails.map(({ Position, Company, Location, Type, Duration }) => (
            <Work
              position={Position}
              company={Company}
              location={Location}
              type={Type}
              duration={Duration}
            />
          ))
        )}
      </section>

      {/* Education Section */}
      <section>
        <h1 className="text-2xl pt-10 text-dark-heading dark:text-light-heading md:text-4xl xl:text-5xl font-bold">
          Education
        </h1>
        {React.Children.toArray(
          eduDetails.map(({ Name, Location, Duration, Extra }) => (
            <div className="mb-6">
              <h2 className="text-xl font-semibold text-dark-heading dark:text-light-heading">
                {Name}
              </h2>
              <p className="text-content">
                {Location} | {Duration}
              </p>
              <p className="text-content italic">{Extra}</p>
            </div>
          ))
        )}
      </section>
    </main>
  );
}

export default About;
