import React from "react";
import { personalDetails } from "../Details";

function Home() {
  const { name, tagline, img } = personalDetails;

  return (
    <main className="container mx-auto max-w-7xl section md:flex justify-between items-center">
      <div>
        <h1 className="text-2xl md:text-4xl xl:text-5xl font-bold text-dark-heading dark:text-light-heading">
          Hi, ðŸ‘‹<br />My Name is
        </h1>
        <h2 className="text-2xl md:text-4xl xl:text-5xl font-bold text-transparent bg-clip-text bg-gradient-to-r from-purple-400 via-pink-500 to-red-500">
          {name}
        </h2>
        <h3 className="text-2xl md:text-4xl xl:text-5xl font-bold text-dark-heading dark:text-light-heading">
          {tagline}
        </h3>
      </div>
      <div className="mt-5 md:mt-0">
        <img className="w-1/2 md:ml-auto" src={img} alt={`Profile picture of ${name}`} />
      </div>
    </main>
  );
}

export default Home;
